### ES6
``` js
class Builder {
    constructor() {
        console.log('Builder Class created!');
    }

    buildPart(partName) {
        console.log('Builder.buildPart invoked!');
    }
}

class ConcreteBuilder extends Builder {
    constructor() {
        super();
        console.log('ConcreteBuilder Class created!');
    }

    buildPart(partName) {
        super.buildPart(partName);
        console.log('ConcreteBuilder.buildPart invoked!');
        this.product = new Product(partName);
    }
    getResult() {
        console.log('ConcreteBuilder.getResult invoked!');
        return this.product;
    }
}

class Product {
    constructor(material) {
        console.log("Product class created");
        this.data = material
    }
}

class Director {
    constructor() {
        this.structure = ['Prod1', 'Prod2', 'Prod3'];
        console.log("Director class created");
    }

    construct() {
        console.log("Director.Construct created");
        for (var prodName in this.structure) {
            let builder = new ConcreteBuilder();
            builder.buildPart(this.structure[prodName]);
            builder.getResult();
        }
    }
}

let director = new Director();
director.construct();

```
---

### Typescript
``` ts
namespace BuilderPattern {
    export class UserBuilder {
        private name: string;
        private age: number;
        private phone: string;
        private address: string;

        constructor(name: string) {
            this.name = name;
        }

        get Name() {
            return this.name;
        }
        setAge(value: number): UserBuilder {
            this.age = value;
            return this;
        }
        get Age() {
            return this.age;
        }
        setPhone(value: string): UserBuilder {
            this.phone = value;
            return this;
        }
        get Phone() {
            return this.phone;
        }
        setAddress(value: string): UserBuilder {
            this.address = value;
            return this;
        }
        get Address() {
            return this.address;
        }

        build(): User {
            return new User(this);
        }
    }

    export class User {
        private name: string;
        private age: number;
        private phone: string;
        private address: string;

        constructor(builder: UserBuilder) {
            this.name = builder.Name;
            this.age = builder.Age;
            this.phone = builder.Phone;
            this.address = builder.Address
        }

        get Name() {
            return this.name;
        }
        get Age() {
            return this.age;
        }
        get Phone() {
            return this.phone;
        }
        get Address() {
            return this.address;
        }
    }

}

