    constructor(list) {
        super();
        this.list = list;
        console.log('ConcreteAggregate Class created');
    }

    createIterator() {
        console.log('ConcreteAggregate.CreateIterator invoked');
        this.iterator = new ConcreteIterator(this);
    }
